    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        //Checks if users exist in Database
        int check=0; 
        email = jTextForgotEmail.getText();
        if(email.isEmpty()){
            check = 1;
            JOptionPane.showMessageDialog(null, "Email cannot be blank!");
        }
        else{
            ResultSet rs = SelectFromDB.getData("SELECT * FROM users WHERE email = '"+email+"' ");
            try {
                if(rs.next()){
                    check=1;
                    jTextForgotSecurityQuestion.setEditable(false);
                    jTextForgotEmail.setEditable(false);
                    jTextForgotSecurityQuestion.setText(rs.getString(4));
                    
                }
                
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, e);
            }
        }
        if(check==0){
            JOptionPane.showMessageDialog(null, "Incorrect Email!");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        int check = 0;
        String securityQuestion = jTextForgotSecurityQuestion.getText();
        String answer = jTextForgotAnswer.getText();
        String newPassword = jPasswordNew.getText();
        if(answer.isEmpty()||newPassword.isEmpty()){
            check = 1;
            JOptionPane.showMessageDialog(null, "All Fields are required!");
        }
        else{
            ResultSet rs = SelectFromDB.getData("SELECT * FROM users WHERE email='"+email+"' and securityQuestion = '"+securityQuestion+"' and answer='"+answer+"'");
            try {
                if(rs.next()){
                    check = 1;
                    //Update password in the database
                    InsertUpdateDelete.setData("UPDATE users SET password='"+newPassword+"' WHERE email = '"+email+"' ", "Password changed successfully!");
                    setVisible(false);
                    new Login().setVisible(true);
                }
                
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, e);
            }
            
        }
        if(check==0){
            JOptionPane.showMessageDialog(null, "Incorrect answer!");
        }
    }//GEN-LAST:event_jButton2ActionPerformed

